import React, { useState } from 'react'

import ExpenseItem from './ExpenseItem'
import ExpensesFilter from './ExpensesFilter'
import Card from '../UI/Card'
import './Expenses.css'

const Expenses = (props) => {
  const [dropdownDate, setDropdownDate] = useState('2020');

  const dropdownDateFilterHandler = selectedDate => {
    setDropdownDate(selectedDate);
    console.log(dropdownDate);
  }

  const filteredExpenses = props.expenseItems.filter(expense => {
    const date = expense.date.getFullYear()
    date === dropdownDate;
    // console.log(`EXPENSE.DATE.FULLYEAR -> ${expense.date.getFullYear()}`);
    // console.log(`DROPDOWNDATE -> ${dropdownDate}`);
  })
  // console.log filteredExpenses
  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter selected={dropdownDate} onDropdownDateFilter={dropdownDateFilterHandler}/>
        {filteredExpenses.map(expense =>(
          <ExpenseItem
            key={expense.id}
            title={expense.title}
            amount={expense.amount}
            date={expense.date}
          />
        ))};
      </Card>
    </div>
  );
}

export default Expenses
