import React from 'react';

import './ExpensesFilter.css';

const ExpensesFilter = (props) => {
  const dateDropdownHandler = event => {
    props.onDropdownDateFilter(event.target.value);
  };

  return (
    <div className='expenses-filter'>
      <div className='expenses-filter__control'>
        <label>Filter by year</label>
        <select onChange={dateDropdownHandler}>
          <option value={props.selected}>2022</option>
          <option value={props.selected}>2021</option>
          <option value={props.selected}>2020</option>
          <option value={props.selected}>2019</option>
        </select>
      </div>
    </div>
  );
};

export default ExpensesFilter;
