import React, { useState } from 'react';

import Card from '../UI/Card';
import Button from './Button';
import cssClasses from '../UI/Card.module.css';

const AddUser = props => {
    const [enteredUsername, setEnteredUsername] = useState('');
    const [enteredAge, setEnteredAge] = useState('');

    const addUserHandler = (event) => {
        event.preventDefault();
        props.onCreateUser(event);
    };

    const buttonClickHandler = event => {
        addUserHandler(event)
    }

    const usernameEnteredHandler = event => {
        console.log(event.target.value)
        // setEnteredUsername()
    };

    const ageEnteredHandler = event => {
        console.log(event.target.value)
    };

    return(
        <Card className={cssClasses.input}>
            <form onSubmit={addUserHandler}>
                <label htmlFor="username">Username</label>
                <input id="username" type="text" onChange={usernameEnteredHandler}></input>
                <label htmlFor="age">Age (Years)</label>
                <input id="age" type="number" onChange={ageEnteredHandler}></input>
                <Button onClick={buttonClickHandler} type="submit">Add User</Button>
            </form>
        </Card>
    )
};

export default AddUser;
